ing bug occured in Firefox\n\nCKEditor 1.0.1\n====================================\n- Fixed fatal error\n- Inherit height of textarea\n\nCKEditor 1.0.0\n====================================\n- Initial commit";s:9:"signature";s:17:"ckeditor-1.3.0-pl";s:13:"initialConfig";s:15:"[object Object]";s:4:"text";s:20:"Продолжить";s:2:"id";s:19:"package-install-btn";s:6:"hidden";s:5:"false";s:7:"handler";s:1749:"function (va){\n        var r;\n        var g = Ext.getCmp(''modx-package-grid'');\n        if (!g) return false;\n        \n        if (va.signature != undefined && va.signature != '''') {\n            r = {signature: va.signature};\n        } else {\n            r = g.menu.record.data ? g.menu.record.data : g.menu.record;\n        }\n		var topic = ''/workspace/package/install/''+r.signature+''/'';\n        g.loadConsole(Ext.getBody(),topic);\n\n		va = va || {};\n        Ext.apply(va,{\n            action: ''workspace/packages/install''\n            ,signature: r.signature\n            ,register: ''mgr''\n            ,topic: topic\n        });\n\n        MODx.Ajax.request({\n            url: MODx.config.connector_url\n            ,params: va\n            ,listeners: {\n                ''success'': {fn:function() {\n                    var bc = Ext.getCmp(''packages-breadcrumbs'');\n                    var trail= bc.data.trail;\n                    trail.pop();\n\n                    if (trail.length > 1) {\n                        last = trail[trail.length - 1];\n\n                        if (last != undefined && last.rec != undefined) {\n                            bc.data.trail.pop();\n                            bc.data.trail.shift();\n                            bc.updateDetail(bc.data);\n\n                            var grid = Ext.getCmp(''modx-package-grid'');\n                            grid.install(last.rec);\n                            return;\n                        }\n                    }\n\n                    this.activate();\n					Ext.getCmp(''modx