INSERT INTO `modx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(1, 0, 0, 'gContainer', '', 0, 2, 0, '<?xml version="1.0" encoding="[[++modx_charset]]"?>\n<urlset xmlns="[[+schema]]">\n[[+items]]</urlset>', 0, 'a:0:{}', 0, ''),
(2, 1, 0, 'html_header', '', 0, 7, 0, '<head>\n    <base href="[[++site_url]]" />\n    <meta charset="[[++modx_charset]]">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <meta name="description" content="">\n    <meta name="author" content="">\n    <title>Captha</title>\n	<!-- core CSS -->\n    <link href="/assets/templates/custom_temp/css/bootstrap.min.css" rel="stylesheet">\n    <link href="/assets/templates/custom_temp/css/font-awesome.min.css" rel="stylesheet">\n      \n    <link href="/assets/templates/custom_temp/css/main.css" rel="stylesheet">\n    \n    <!--[if lt IE 9]>\n    <script src="/assets/templates/main_temp/js/html5shiv.js"></script>\n    <script src="/assets/templates/main_temp/js/respond.min.js"></script>\n    <![endif]-->       \n    <link rel="shortcut icon" href="/assets/templates/images/ico/favicon.ico">\n    <link rel="stylesheet" href="/assets/components/Captha_test/css/captcha.css"/>\n    <script src="/assets/templates/custom_temp/js/jquery.js"></script>\n    <script src="/assets/templates/custom_temp/js/bootstrap.min.js"></script>\n    \n   \n    \n</head><!--/head-->', 0, 'a:0:{}', 0, ''),
(19, 2, 0, 'custom_lincks_unregister_users', '', 0, 16, 0, '<div>\n    <p>\n     <a href="/vosstanovlenie-parolya">Забыли пароль?</a>   \n    </p>\n    <p>\n      <a href="/registracziya">Зарегистрироваться</a>  \n    </p>\n    \n</div>', 0, 'a:0:{}', 0, ''),
(3, 1, 0, 'header', '', 0, 7, 0, '\n <header id="header">\n     <nav id="main-menu" class="navbar navbar-inverse navbar-fixed-top" role="banner">\n            <div class="container">\n                <div class="navbar-header">\n                    <button type="button" class="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse">\n                        <span class="sr-only">Toggle navigation</span>\n                        <span class="icon-bar"></span>\n                        <span class="icon-bar"></span>\n                        <span class="icon-bar"></span>\n                    </button>\n                    \n                </div>\n				\n                <div class="collapse navbar-collapse navbar-right" id=''scroll''>\n                    <ul class="nav navbar-nav" >\n                        <li class="scroll"><a href="/">Вернутся на сайт</a></li>\n                        <li class="scroll"><a href="/lichnyij-kabinet">Информация о пользователе</a></li>\n                        <li class="scroll"><a href="/lichnyij-kabinet/izmenenie-parolya">Изменить пароль</a></li>\n                        <li class="scroll"><a href="/lichnyij-kabinet/nastrojka-akkaunta">Редактировать данные</a></li>\n                        <li class="scroll"><a href="/lichnyij-kabinet/statistika-rezultatov">Статистика результатов</a></li>\n                    </ul>\n                </div>\n            </div><!--/.container-->\n        </nav>\n       \n    </header><!--/header-->', 0, 'a:0:{}', 0, ''),
(6, 0, 0, 'lgnLogoutTpl', '', 0, 15, 0, '<div class="loginMessage">[[+errors]]</div>\n<br />\n<div class="loginLogin">\n    <div class="loginRegister">\n        <a href="[[+logoutUrl]]" title="[[+actionMsg]]">[[+actionMsg]]</a>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(7, 0, 0, 'lgnErrTpl', '', 0, 15, 0, '<p class="error">[[+msg]]</p>\n', 0, 'a:0:{}', 0, ''),
(8, 0, 0, 'lgnForgotPassEmail', '', 0, 15, 0, '<p>[[+username]],</p>\n\n<p>To activate your new password, please click on the following link:</p>\n\n<p><a href="[[+confirmUrl]]">[[+confirmUrl]]</a></p>\n\n<p>If successful you can use the following password to login:</p>\n\n<p><strong>Password:</strong> [[+password]]</p>\n\n<p>If you did not request this message, please ignore it.</p>\n\n<p>Thanks,<br />\n<em>Site Administrator</em></p>', 0, 'a:0:{}', 0, ''),
(9, 0, 0, 'lgnForgotPassSentTpl', '', 0, 15, 0, '<h2>Your Login Information Has Been Sent</h2>\n\n<p>Your login information has been sent to the email address [[+email]].</p>', 0, 'a:0:{}', 0, ''),
(10, 0, 0, 'lgnForgotPassTpl', '', 0, 15, 0, '<div class="loginFPErrors">[[+loginfp.errors]]</div>\n<div class="loginFP">\n    <form class="loginFPForm" action="[[~[[*id]]]]" method="post">\n        <fieldset class="loginFPFieldset">\n            <legend class="loginFPLegend">[[%register.forgot_password]]</legend>\n            <label class="loginFPUsernameLabel">[[%login.username]]\n                <input class="loginFPUsername" type="text" name="username" value="[[+loginfp.post.username]]" />\n            </label>\n            \n            <p>[[%login.or_forgot_username]]</p>\n            \n            <label class="loginFPEmailLabel">[[%login.email]]\n                <input class="loginFPEmail" type="text" name="email" value="[[+loginfp.post.email]]" />\n            </label>\n            \n            <input class="returnUrl" type="hidden" name="returnUrl" value="[[+loginfp.request_uri]]" />\n            \n            <input class="loginFPService" type="hidden" name="login_fp_service" value="forgotpassword" />\n            <span class="loginFPButton"><input type="submit" name="login_fp" value="[[%login.reset_password]]" /></span>\n        </fieldset>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(4, 1, 0, 'footer', '', 0, 8, 0, '<footer id="footer" class="footer">\n        <div class="container">\n            <div class="row">\n                <div class="col-sm-6">\n                    &copy; 2017 [[++site_name]]\n                </div>\n                \n            </div>\n        </div>\n    </footer><!--/#footer-->\n     <script src="/assets/templates/custom_temp/js/jquery.js"></script>\n    <script src="/assets/templates/custom_temp/js/bootstrap.min.js"></script>\n   <script src="/assets/templates/custom_temp/js/active_class.js"></script>\n   \n    <script src="/assets/templates/custom_temp/js/wow.min.js"></script>\n    <script src="/assets/templates/custom_temp/js/main.js"></script>\n   <!-- скрипт для прижатия футера к нижней части страници в случае когда контент не заполняет всю страницу.\n   скприпт запускается только когда размер документа меньше размера окна-->\n     <script type="text/javascript">\n        if ($(document).height() <= $(window).height()) \n         $("footer.footer").addClass("navbar-fixed-bottom");\n     </script>\n     <script>\n     on("click", ".scroll", alert(''test''));\n         $("#scroll").on("click", ".scroll", function(){\n        $("#scroll .scroll").removeClass("active"); //удаляем класс во всех вкладках\n        $(this).addClass("active"); //добавляем класс текущей (нажатой)\n             });\n     </script>\n     \n    \n<script  type="text/javascript" src="/assets/components/Captha_test/js/captha.js"></script>', 0, 'a:0:{}', 0, ''),
(20, 2, 0, 'panel_footer_menu', '', 0, 8, 0, ' <hr>\n      <div class="col-xs-4" style="margin-bottom:8px;">\n       <i class="glyphicon glyphicon-pencil"></i> <a href="[[~17]]" title="Изменение пароля">Изменение пароля</a>\n      </div>\n      <div class="col-xs-4" style="margin-bottom:8px;">\n        <i class="glyphicon glyphicon-cog"></i> <a href="[[~13]]" title="Редактирование данных">Редактирование данных</a>\n      </div>\n      <div class="col-xs-4" style="margin-bottom:8px;">\n        <i class="glyphicon  glyphicon-stats"></i> <a href="[[~19]]" title="Статистика результатов">Статистика результатов</a>\n      </div>', 0, 'a:0:{}', 0, ''),
(5, 0, 0, 'lgnLoginTpl', '', 0, 15, 0, '<div class="loginForm">\n    <div class="loginMessage">[[+errors]]</div>\n    <div class="loginLogin">\n        <form class="loginLoginForm" action="[[~[[*id]]]]" method="post">\n            <fieldset class="loginLoginFieldset">\n                <legend class="loginLegend">[[+actionMsg]]</legend>\n                <label class="loginUsernameLabel">[[%login.username]]\n                    <input class="loginUsername" type="text" name="username" />\n                </label>\n                \n                <label class="loginPasswordLabel">[[%login.password]]\n                    <input class="loginPassword" type="password" name="password" />\n                </label>\n                <input class="returnUrl" type="hidden" name="returnUrl" value="[[+request_uri]]" />\n\n                [[+login.recaptcha_html]]\n                \n                <input class="loginLoginValue" type="hidden" name="service" value="login" />\n                <span class="loginLoginButton"><input type="submit" name="Login" value="[[+actionMsg]]" /></span>\n            </fieldset>\n        </form>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(11, 0, 0, 'lgnResetPassTpl', '', 0, 15, 0, '<div class="loginResetPass">\n<p class="loginResetPassHeader">[[+username]],</p>\n\n<p class="loginResetPassText">Your password has been reset. Please return <a href="[[+loginUrl]]">here</a> to log in.</p>  \n</div>', 0, 'a:0:{}', 0, ''),
(12, 0, 0, 'lgnRegisterFormTpl', '', 0, 15, 0, '<div class="register">\n    <div class="registerMessage">[[+error.message]]</div>\n    \n    <form class="form" action="[[~[[*id]]]]" method="post">\n        <input type="hidden" name="nospam:blank" value="" />\n        \n        <label for="username">[[%register.username? &namespace=`login` &topic=`register`]]\n            <span class="error">[[+error.username]]</span>\n        </label>\n        <input type="text" name="username:required:minLength=6" id="username" value="[[+username]]" />\n        \n        <label for="password">[[%register.password]]\n            <span class="error">[[+error.password]]</span>\n        </label>\n        <input type="password" name="password:required:minLength=6" id="password" value="[[+password]]" />\n        \n        <label for="password_confirm">[[%register.password_confirm]]\n            <span class="error">[[+error.password_confirm]]</span>\n        </label>\n        <input type="password" name="password_confirm:password_confirm=`password`" id="password_confirm" value="[[+password_confirm]]" />\n        \n        <label for="fullname">[[%register.fullname]]\n            <span class="error">[[+error.fullname]]</span>\n        </label>\n        <input type="text" name="fullname:required" id="fullname" value="[[+fullname]]" />\n        \n        <label for="email">[[%register.email]]\n            <span class="error">[[+error.email]]</span>\n        </label>\n        <input type="text" name="email:email" id="email" value="[[+email]]" />\n        \n        <br class="clear" />\n\n        [[+register.recaptcha_html]]\n        [[+error.recaptcha]]\n        \n        <div class="form-buttons">\n            <input type="submit" name="registerbtn" value="Register" />\n        </div>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(13, 0, 0, 'lgnActivateEmailTpl', '', 0, 15, 0, '<p>[[+username]],</p>\n\n<p>Thanks for registering! To activate your new account, please click on the following link:</p>\n\n<p><a href="[[+confirmUrl]]">[[+confirmUrl]]</a></p>\n\n<p>After activating, you may login with your password and username:</p>\n\n<p>\nUsername: <strong>[[+username]]</strong><br />\nPassword: <strong>[[+password]]</strong></p>\n\n<p>If you did not request this message, please ignore it.</p>\n\n<p>Thanks,<br />\n<em>Site Administrator</em></p>', 0, 'a:0:{}', 0, ''),
(14, 0, 0, 'lgnActiveUser', '', 0, 15, 0, '<li>[[+username]]</li>', 0, 'a:0:{}', 0, ''),
(15, 0, 0, 'lgnResetPassChangePassTpl', '', 0, 15, 0, '[[!+logcp.error_message:notempty=`<p style="color: red;">[[+logcp.error_message]]</p>`]]\n\n<form class="form inline" action="" method="post">\n    <input type="hidden" name="nospam:blank" value="" />\n\n    <div class="ff">\n        <label for="password_new">[[!%login.password_new]]\n            <span class="error">[[+logcp.error.password_new]]</span>\n        </label>\n        <input type="password" name="password_new:required" id="password_new" value="[[+logcp.password_new]]" />\n    </div>\n\n    <div class="ff">\n        <label for="password_new_confirm">[[!%login.password_new_confirm]]\n            <span class="error">[[+logcp.error.password_new_confirm]]</span>\n        </label>\n        <input type="password" name="password_new_confirm:required" id="password_new_confirm" value="[[+logcp.password_new_confirm]]" />\n    </div>\n\n    <br class="clear" />\n\n    <div class="form-buttons">\n        <input type="submit" name="logcp-submit" value="[[!%login.change_password]]" />\n    </div>\n</form>', 0, 'a:0:{}', 0, ''),
(16, 0, 0, 'lgnExpiredTpl', '', 0, 15, 0, '<p><strong>Password Reset Information</strong></p>\n<p>Your password has already been reset or the link expired. If you need to reset your password again, click <a href="#">here</a>.</p>', 0, 'a:0:{}', 0, ''),
(17, 2, 0, 'customRegisterFormTpl', '', 0, 16, 0, '<div class="register">\n    <div class="registerMessage">[[+error.message]]</div>\n    \n    <form class="form" action="[[~[[*id]]]]" method="post">\n        <input type="hidden" name="nospam" value="" />\n        \n        <label for="username">[[%register.username? &namespace=`login` &topic=`register`]]\n            <span class="error">[[+error.username]]</span>\n        </label>\n        <input type="text" name="username" id="username" value="[[+username]]" />\n        \n        <label for="password">[[%register.password]]\n            <span class="error">[[+error.password]]</span>\n        </label>\n        <input type="password" name="password" id="password" value="[[+password]]" />\n        \n        <label for="password_confirm">[[%register.password_confirm]]\n            <span class="error">[[+error.password_confirm]]</span>\n        </label>\n        <input type="password" name="password_confirm" id="password_confirm" value="[[+password_confirm]]" />\n        \n       \n        <label for="email">[[%register.email]]\n            <span class="error">[[+error.email]]</span>\n        </label>\n        <input type="text" name="email" id="email" value="[[+email]]" />\n        \n        <br class="clear" />\n\n        [[+register.recaptcha_html]]\n        [[+error.recaptcha]]\n        \n        <div class="form-buttons">\n            <input type="submit" name="registerbtn" value="Зарегистрироваться" />\n        </div>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(18, 0, 0, 'customActivateEmailTpl', '', 0, 16, 0, '<p>[[+username]],</p>\n\n<p>Спасибо за регистрацию!</p>\n<p>Для активации вашего аккаунта, пожалуйста перейдите по следующей ссылке:</p>\n\n<p><a href="[[+confirmUrl]]">[[+confirmUrl]]</a></p>\n\n<p>После активации аккаунта вы сможете зайти на сайт используя следующий логин и пароль:</p>\n\n<p>\nЛогин: <strong>[[+username]]</strong><br />\nПароль: <strong>[[+password]]</strong></p>\n\n\n<p>Спасибо,<br />\n<em>Администрация сайта "[[++site_name]]" </em></p>', 0, 'a:0:{}', 0, '');
INSERT INTO `modx_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(1, 0, 0, 'phpThumbOfCacheManager', 'Handles cache cleaning when clearing the Site Cache.', 0, 13, 0, '/*\n * Handles cache cleanup\n * pThumb\n * Copyright 2013 Jason Grant\n *\n * Please see the GitHub page for documentation or to report bugs:\n * https://github.com/oo12/phpThumbOf\n *\n * pThumb is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * pThumb is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * phpThumbOf; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n */\n\nif ($modx->event->name === ''OnSiteRefresh'') {\n	if (!$modx->loadClass(''pThumbCacheCleaner'', MODX_CORE_PATH . ''components/phpthumbof/model/'', true, true)) {\n		$modx->log(modX::LOG_LEVEL_ERROR, ''[pThumb] Could not load pThumbCacheCleaner class.'');\n		return;\n	}\n	static $pt_settings = array();\n	$pThumb = new pThumbCacheCleaner($modx, $pt_settings, array(), true);\n	$pThumb->cleanCache();\n}', 0, NULL, 0, '', 0, ''),
(2, 0, 0, 'Ace', 'Ace code editor plugin for MODx Revolution', 0, 0, 0, '/**\n * Ace Source Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnSnipFormPrerender,\n * OnTempFormPrerender, OnChunkFormPrerender, OnPluginFormPrerender,\n * OnFileCreateFormPrerender, OnFileEditFormPrerender, OnDocFormPrerender\n *\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ace\n *\n * @var array $scriptProperties\n * @var Ace $ace\n */\nif ($modx->event->name == ''OnRichTextEditorRegister'') {\n    $modx->event->output(''Ace'');\n    return;\n}\n\nif ($modx->getOption(''which_element_editor'', null, ''Ace'') !== ''Ace'') {\n    return;\n}\n\n$ace = $modx->getService(''ace'', ''Ace'', $modx->getOption(''ace.core_path'', null, $modx->getOption(''core_path'').''components/ace/'').''model/ace/'');\n$ace->initialize();\n\n$extensionMap = array(\n    ''tpl''   => ''text/x-smarty'',\n    ''htm''   => ''text/html'',\n    ''html''  => ''text/html'',\n    ''css''   => ''text/css'',\n    ''scss''  => ''text/x-scss'',\n    ''less''  => ''text/x-less'',\n    ''svg''   => ''image/svg+xml'',\n    ''xml''   => ''application/xml'',\n    ''xsl''   => ''application/xml'',\n    ''js''    => ''application/javascript'',\n    ''json''  => ''application/json'',\n    ''php''   => ''application/x-php'',\n    ''sql''   => ''text/x-sql'',\n    ''md''    => ''text/x-markdown'',\n    ''txt''   => ''text/plain'',\n    ''twig''  => ''text/x-twig''\n);\n\n// Defines wether we should highlight modx tags\n$modxTags = false;\nswitch ($modx->event->name) {\n    case ''OnSnipFormPrerender'':\n        $field = ''modx-snippet-snippet'';\n        $mimeType = ''application/x-php'';\n        break;\n    case ''OnTempFormPrerender'':\n        $field = ''modx-template-content'';\n        $modxTags = true;\n\n        switch (true) {\n            case $modx->getOption(''twiggy_class''):\n                $mimeType = ''text/x-twig'';\n                break;\n            case $modx->getOption(''pdotools_fenom_parser''):\n                $mimeType = ''text/x-smarty'';\n                break;\n            default:\n                $mimeType = ''text/html'';\n                break;\n        }\n\n        break;\n    case ''OnChunkFormPrerender'':\n        $field = ''modx-chunk-snippet'';\n        if ($modx->controller->chunk && $modx->controller->chunk->isStatic()) {\n            $extension = pathinfo($modx->controller->chunk->getSourceFile(), PATHINFO_EXTENSION);\n            $mimeType = isset($extensionMap[$extension]) ? $extensionMap[$extension] : ''text/plain'';\n        } else {\n            $mimeType = ''text/html'';\n        }\n        $modxTags = true;\n\n        switch (true) {\n            case $modx->getOption(''twiggy_class''):\n                $mimeType = ''text/x-twig'';\n                break;\n            case $modx->getOption(''pdotools_fenom_default''):\n                $mimeType = ''text/x-smarty'';\n                break;\n            default:\n                $mimeType = ''text/html'';\n                break;\n        }\n\n        break;\n    case ''OnPluginFormPrerender'':\n        $field = ''modx-plugin-plugincode'';\n        $mimeType = ''application/x-php'';\n        break;\n    case ''OnFileCreateFormPrerender'':\n        $field = ''modx-file-content'';\n        $mimeType = ''text/plain'';\n        break;\n    case ''OnFileEditFormPrerender'':\n        $field = ''modx-file-content'';\n        $extension = pathinfo($scriptProperties[''file''], PATHINFO_EXTENSION);\n        $mimeType = isset($extensionMap[$extension])\n            ? $extensionMap[$extension]\n            : ''text/plain'';\n        $modxTags = $extension == ''tpl'';\n        break;\n    case ''OnDocFormPrerender'':\n        if (!$modx->controller->resourceArray) {\n            return;\n        }\n        $field = ''ta'';\n        $mimeType = $modx->getObject(''modContentType'', $modx->controller->resourceArray[''content_type''])->get(''mime_type'');\n\n        switch (true) {\n            case $mimeType == ''text/html'' && $modx->getOption(''twiggy_class''):\n                $mimeType = ''text/x-twig'';\n                break;\n            case $mimeType == ''text/html'' && $modx->getOption(''pdotools_fenom_parser''):\n                $mimeType = ''text/x-smarty'';\n                break;\n        }\n\n        if ($modx->getOption(''use_editor'')){\n            $richText = $modx->controller->resourceArray[''richtext''];\n            $classKey = $modx->controller->resourceArray[''class_key''];\n            if ($richText || in_array($classKey, array(''modStaticResource'',''modSymLink'',''modWebLink'',''modXMLRPCResource''))) {\n                $field = false;\n            }\n        }\n        $modxTags = true;\n        break;\n    default:\n        return;\n}\n\n$modxTags = (int) $modxTags;\n$script = '''';\nif ($field) {\n    $script .= "MODx.ux.Ace.replaceComponent(''$field'', ''$mimeType'', $modxTags);";\n}\n\nif ($modx->event->name == ''OnDocFormPrerender'' && !$modx->getOption(''use_editor'')) {\n    $script .= "MODx.ux.Ace.replaceTextAreas(Ext.query(''.modx-richtext''));";\n}\n\nif ($script) {\n    $modx->controller->addHtml(''<script>Ext.onReady(function() {'' . $script . ''});</script>'');\n}', 0, NULL, 0, '', 0, 'ace/elements/plugins/ace.plugin.php'),
