(10, 0, 0, 'user_account', '', 0, 17, '', 0, '<!DOCTYPE html>\n<html>\n    [[$html_header]]\n    <body id="home" class="homepage">\n      \n       [[$header]]\n       \n       <div class="container">\n            <div class="section-header">\n            \n                <h1 class="section-title text-center">[[*pagetitle]]</h1>\n            </div>\n           \n\n[[!Profile? &prefix=`usr.`]]\n \n<div class="panel panel-primary">\n  <div class="panel-heading"><i class="glyphicon glyphicon-edit"></i> Личный кабинет пользователя</div>\n    <div class="panel-body">\n      <p>Имя пользователя: [[+usr.username]]</p>\n      <p>Телефон: [[+usr.phone]]</p>\n      <p>Мобильный телефон: [[+usr.mobilephone]]</p>\n      <p>Email: [[+usr.email]]</p>\n      <hr>\n      <p>Адрес: [[+usr.address]]</p>\n      <p>Страна: [[+usr.country]]</p>\n      <p>Город: [[+usr.city]]</p>\n      <p>Веб-сайт: [[+usr.website]]</p>\n      <hr>\n      <div class="col-xs-4" style="margin-bottom:8px;">\n       <i class="glyphicon glyphicon-pencil"></i> <a href="[[~17]]" title="Изменение пароля">Изменение пароля</a>\n      </div>\n      <div class="col-xs-4" style="margin-bottom:8px;">\n        <i class="glyphicon glyphicon-cog"></i> <a href="[[~13]]" title="Редактирование данных">Редактирование данных</a>\n      </div>\n      <div class="col-xs-4" style="margin-bottom:8px;">\n        <i class="glyphicon glyphicon-list-alt"></i> <a href="[[~19]]" title="Статистика результатов">Статистика результатов</a>\n      </div>\n    </div>\n  </div>\n</div>\n        \n        </div> \n       \n     [[$footer]]   \n\n    </body>\n    \n    \n</html>', 0, 'a:0:{}', 0, ''),
(11, 2, 0, 'user_data_change', '', 0, 17, '', 0, '<!DOCTYPE html>\n<html>\n    [[$html_header]]\n    <body id="home" class="homepage">\n      \n       [[$header]]\n       \n       <div class="container">\n            <div class="section-header">\n            \n                <h1 class="section-title text-center">[[*pagetitle]]</h1>\n            </div>\n            \n            [[!UpdateProfile? &validate=`fullname:required`]]\n \n<div class="container">\n  <div class="row">\n    <div class="col-md-10 col-lg-10 col-md-offset-1 col-lg-offset-1">\n      <div class="panel panel-primary">\n        <div class="panel-heading"><i class="glyphicon glyphicon-edit"></i> Редактирование данных</div>\n        <div class="panel-body">\n          <div class="updprof-error">[[+error.message]]</div>\n          [[+login.update_success:is=`1`:then=`[[%login.profile_updated? &namespace=`login` &topic=`updateprofile`]]`]]\n          <form action="[[~[[*id]]]]" method="post" class="form-horizontal">\n            <input type="hidden" name="nospam" value="">\n            <div class="form-group">\n              <label for="fullname" class="col-sm-3 control-label">[[!%login.fullname? &namespace=`login` &topic=`updateprofile`]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="fullname" class="form-control" id="fullname" value="[[+fullname]]">\n                <span class="help-block text-error">\n                  [[+error.fullname]]\n                </span>        \n              </div>      \n            </div>\n            <div class="form-group">\n              <label for="phone" class="col-sm-3 control-label">[[!%login.phone]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="phone" class="form-control" id="phone" value="[[+phone]]">\n                <span class="help-block text-error">\n                  [[+error.phone]]\n                </span>        \n              </div>      \n            </div> \n            <div class="form-group">\n              <label for="mobilephone" class="col-sm-3 control-label">[[!%login.mobilephone]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="mobilephone" class="form-control" id="mobilephone" value="[[+mobilephone]]">\n                <span class="help-block text-error">\n                  [[+error.mobilephone]]\n                </span>        \n              </div>      \n            </div> \n            <div class="form-group">\n              <label for="address" class="col-sm-3 control-label">[[!%login.address]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="address" class="form-control" id="address" value="[[+address]]">\n                <span class="help-block text-error">\n                  [[+error.address]]\n                </span>        \n              </div>      \n            </div>  \n            <div class="form-group">\n              <label for="country" class="col-sm-3 control-label">[[!%login.country]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="country" class="form-control" id="country" value="[[+country]]">\n                <span class="help-block text-error">\n                  [[+error.country]]\n                </span>        \n              </div>      \n            </div>   \n            <div class="form-group">\n              <label for="city" class="col-sm-3 control-label">[[!%login.city]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="city" class="form-control" id="city" value="[[+city]]">\n                <span class="help-block text-error">\n                  [[+error.city]]\n                </span>        \n              </div>      \n            </div>  \n            <div class="form-group">\n              <label for="website" class="col-sm-3 control-label">[[!%login.website]]</label>\n              <div class="col-sm-7">\n                <input type="text" name="website" class="form-control" id="website" value="[[+website]]">\n                <span class="help-block text-error">\n                  [[+error.website]]\n                </span>        \n              </div>      \n            </div>  \n            <div class="clearfix"></div>\n           \n                   <div class="col-sm-10">\n                  <input type="submit" value="[[!%login.update_profile]]" name="login-updprof-btn" id="login-updprof-btn" class="btn btn-primary pull-right">\n         </div>\n          </form>\n          <div class="clearfix"></div>\n          [[$panel_footer_menu]]\n        </div>\n        \n      </div>\n    </div>\n  </div>\n</div>\n            \n        </div> \n       \n     [[$footer]]   \n\n    </body>\n    \n    \n</html>', 0, 'a:0:{}', 0, ''),
